```kotlin:app/src/main/java/com/example/test/ui/screens/UmkmScreen.kt
package com.example.test.ui.screens

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

// Data class untuk UMKM
data class Umkm(
 val id: String = "",
 val nama: String = "",
 val alamat: String = "",
 val kategori: String = "",
 // Tambahkan field lain sesuai kebutuhan
)

// ViewModel untuk UMKM
class UmkmViewModel : ViewModel() {
 private val firestore = FirebaseFirestore.getInstance()
 private val _umkmList = MutableStateFlow<List<Umkm>>(emptyList())
 val umkmList = _umkmList.asStateFlow()

 private val _isLoading = MutableStateFlow(true)
 val isLoading = _isLoading.asStateFlow()

 init {
 getUmkmData()
 }

 private fun getUmkmData() {
 viewModelScope.launch {
 try {
 _isLoading.value = true
 val snapshot = firestore.collection("umkm").get().await()
 val umkmList = snapshot.documents.mapNotNull { doc ->
 doc.toObject(Umkm::class.java)?.copy(id = doc.id)
 }
 _umkmList.value = umkmList
 } catch (e: Exception) {
 // Handle error
 println("Error fetching UMKM data: ${e.message}")
 } finally {
 _isLoading.value = false
 }
 }
 }
}

@Composable
fun UmkmScreen(
 viewModel: UmkmViewModel
) {
 val umkmList by viewModel.umkmList.collectAsState()
 val isLoading by viewModel.isLoading.collectAsState()

 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp)
 ) {
 Text(
 text = "Daftar UMKM",
 style = MaterialTheme.typography.headlineMedium,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 if (isLoading) {
 Box(modifier = Modifier.fillMaxSize()) {
 CircularProgressIndicator()
 }
 } else {
 LazyColumn(
 verticalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 items(umkmList) { umkm ->
 UmkmItem(umkm)
 }
 }
 }
 }
}

@Composable
fun UmkmItem(umkm: Umkm) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp)
 ) {
 Column(
 modifier = Modifier
 .padding(16.dp)
 .fillMaxWidth()
 ) {
 Text(
 text = umkm.nama,
 style = MaterialTheme.typography.titleMedium
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = umkm.alamat,
 style = MaterialTheme.typography.bodyMedium
 )
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 text = "Kategori: ${umkm.kategori}",
 style = MaterialTheme.typography.bodySmall
 )
 }
 }
}
```

