```kotlin:app/src/main/java/com/example/test/ui/viewModels/UmkmViewModel.kt
class UmkmViewModel : ViewModel() {
 private val firestore = FirebaseFirestore.getInstance()
 private val _umkmList = MutableStateFlow<List<Umkm>>(emptyList())
 val umkmList = _umkmList.asStateFlow()

 private val _isLoading = MutableStateFlow(true)
 val isLoading = _isLoading.asStateFlow()

 private val _error = MutableStateFlow<String?>(null)
 val error = _error.asStateFlow()

 init {
 getUmkmData()
 }

 private fun getUmkmData() {
 viewModelScope.launch {
 try {
 _isLoading.value = true
 _error.value = null

 val snapshot = firestore.collection("umkm")
 .orderBy("registrationDate", Query.Direction.DESCENDING)
 .get()
 .await()

 val umkmList = snapshot.documents.mapNotNull { doc ->
 try {
 val data = doc.data
 if (data != null) {
 val addressMap = data["address"] as? Map<String, Any>
 val address = Address(
 street = addressMap?.get("street") as? String ?: "",
 city = addressMap?.get("city") as? String ?: "",
 province = addressMap?.get("province") as? String ?: "",
 postalCode = addressMap?.get("postalCode") as? String ?: ""
 )

 Umkm(
 umkmId = doc.id,
 ownerId = data["ownerId"] as? String ?: "",
 name = data["name"] as? String ?: "",
 businessType = data["businessType"] as? String ?: "",
 description = data["description"] as? String ?: "",
 address = address,
 contact = data["contact"] as? String ?: "",
 registrationDate = (data["registrationDate"] as? Long) 
 ?: System.currentTimeMillis(),
 status = data["status"] as? String ?: "active"
 )
 } else null
 } catch (e: Exception) {
 Log.e("UmkmViewModel", "Error parsing UMKM document: ${e.message}")
 null
 }
 }
 _umkmList.value = umkmList
 } catch (e: Exception) {
 Log.e("UmkmViewModel", "Error fetching UMKM data: ${e.message}")
 _error.value = "Gagal memuat data UMKM: ${e.message}"
 } finally {
 _isLoading.value = false
 }
 }
 }

 fun checkUmkmExistence(memberId: String, onResult: (Boolean, String?) -> Unit) {
 if (memberId.isBlank()) {
 onResult(false, "Member ID tidak valid")
 return
 }

 viewModelScope.launch {
 try {
 val memberDoc = firestore.collection("members")
 .document(memberId)
 .get()
 .await()

 if (!memberDoc.exists()) {
 onResult(false, "Member tidak ditemukan")
 return@launch
 }

 val umkmIds = memberDoc.get("umkmIds") as? List<String>
 if (umkmIds.isNullOrEmpty()) {
 onResult(false, "Member belum memiliki UMKM")
 return@launch
 }

 val umkmQuery = firestore.collection("umkm")
 .whereEqualTo("memberId", memberId)
 .get()
 .await()

 if (umkmQuery.isEmpty) {
 onResult(false, "UMKM tidak ditemukan")
 } else {
 onResult(true, "UMKM sudah terdaftar")
 }
 } catch (e: Exception) {
 Log.e("UmkmViewModel", "Error checking UMKM existence: ${e.message}")
 onResult(false, "Error: ${e.message}")
 }
 }
 }

 fun refreshData() {
 getUmkmData()
 }
}
```

