 "umkmId" to umkmId,
 "memberId" to memberId,
 "name" to name,
 "businessType" to businessType,
 "description" to description,
 "imageUrl" to imageUrl,
 "address" to mapOf(
 "street" to street,
 "village" to village,
 "subDistrict" to subDistrict,
 "city" to city,
 "province" to province,
 "postalCode" to postalCode
 ),
