```kotlin:app/src/main/java/com/example/test/ui/viewModels/NewsViewModel.kt
fun fetchNews(
 isLoadMore: Boolean = false,
 onLoading: () -> Unit = {},
 onSuccess: (List<News>) -> Unit,
 onError: (String) -> Unit
) {
 if (isLastPage && isLoadMore) {
 return // Jika sudah halaman terakhir dan mencoba load more, hentikan
 }

 onLoading()
 var query = newsCollection
 .orderBy("createdAt", Query.Direction.DESCENDING)
 .limit(pageSize)

 // Jika load more, gunakan startAfter dengan dokumen terakhir
 if (isLoadMore && lastVisibleNews != null) {
 query = query.startAfter(lastVisibleNews!!)
 } else if (isLoadMore) {
 return // Jika load more tapi tidak ada lastVisible, hentikan
 }

 val listener = query.addSnapshotListener { snapshot, error ->
 if (error != null) {
 onError(error.message ?: "Gagal mengambil berita")
 return@addSnapshotListener
 }

 val documents = snapshot?.documents ?: emptyList()
 val newsList = documents.mapNotNull { it.toObject(News::class.java) }

 // Update pagination state
 if (documents.isNotEmpty()) {
 lastVisibleNews = documents[documents.size - 1]
 isLastPage = documents.size < pageSize
 } else {
 isLastPage = true
 }

 onSuccess(newsList)
 }
 listeners.add(listener)
}
