 var province by remember { mutableStateOf("") }

 // State untuk varian
 var variants by remember { mutableStateOf<List<ProductVariant>>(emptyList()) }
 var showVariantDialog by remember { mutableStateOf(false) }

 // Loading kategori saat komponen dimuat
 LaunchedEffect(Unit) {
 viewModel.fetchCategories { fetchedCategories ->
 categories = fetchedCategories
 }
 }

 val imagePickerLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.GetMultipleContents()
 ) { uris -> 
 selectedImages = uris 
 }

 Scaffold(
 topBar = {
 TopAppBar(
 title = { Text("Tambah Produk") },
 navigationIcon = {
 IconButton(onClick = { navController.popBackStack() }) {
 Icon(Icons.AutoMirrored.Filled.ArrowBack, "Kembali")
 }
 }
 )
 }
 ) { paddingValues ->
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .verticalScroll(rememberScrollState())
 ) {
 // Basic Info Section
 OutlinedTextField(
 value = productName,
 onValueChange = { productName = it },
 label = { Text("Nama Produk") },
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 )

 // Category Dropdown
 ExposedDropdownMenuBox(
 expanded = false,
 onExpandedChange = {},
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 OutlinedTextField(
 value = selectedCategory?.name ?: "",
 onValueChange = {},
 readOnly = true,
 label = { Text("Kategori") },
 trailingIcon = { ExposedDropdownMenuDefaults.TrailingIcon(expanded = false) },
 modifier = Modifier.menuAnchor()
 )

 DropdownMenu(
 expanded = false,
 onDismissRequest = {}
 ) {
 categories.forEach { category ->
 DropdownMenuItem(
 text = { Text(category.name) },
 onClick = {
 selectedCategory = category
 selectedSubcategory = null
 }
 )
 }
 }
 }

 // Price and Stock Section
 OutlinedTextField(
 value = price,
 onValueChange = { price = it },
 label = { Text("Harga") },
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 )

 OutlinedTextField(
 value = stock,
 onValueChange = { stock = it },
 label = { Text("Stok") },
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 )

 // Variants Section
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text("Varian Produk", style = MaterialTheme.typography.titleMedium)
 Button(onClick = { showVariantDialog = true }) {
 Text("Tambah Varian")
 }
 }

 LazyRow(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 items(variants) { variant ->
 Card(
 modifier = Modifier.padding(vertical = 4.dp)
 ) {
 Column(
 modifier = Modifier.padding(8.dp)
 ) {
 Text(variant.name)
 Text("Rp ${variant.price}")
 Text("Stok: ${variant.stock}")
 }
 }
 }
 }
 }

 // Location Section
 OutlinedTextField(
 value = address,
 onValueChange = { address = it },
 label = { Text("Alamat") },
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 )

 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 OutlinedTextField(
 value = city,
 onValueChange = { city = it },
 label = { Text("Kota") },
 modifier = Modifier.weight(1f)
 )
 OutlinedTextField(
 value = province,
 onValueChange = { province = it },
 label = { Text("Provinsi") },
 modifier = Modifier.weight(1f)
 )
 }

 // Media Section
 Button(
 onClick = { imagePickerLauncher.launch("image/*") },
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 Text("Pilih Gambar Produk")
 }

 LazyRow(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 items(selectedImages) { uri ->
 AsyncImage(
 model = uri,
 contentDescription = null,
 modifier = Modifier
 .size(100.dp)
 .padding(4.dp)
 )
 }
 }

 // Save Button
 Button(
 onClick = {
 val product = Product(
 name = productName,
 description = description,
 categoryId = selectedCategory?.id ?: "",
 subcategoryId = selectedSubcategory?.id ?: "",
 price = price.toDoubleOrNull() ?: 0.0,
 stock = stock.toIntOrNull() ?: 0,
 sellerId = viewModel.getCurrentUserId() ?: "",
 location = ProductLocation(
 address = address,
 city = city,
 province = province
 ),
 variants = variants
 )

 viewModel.addProduct(product, selectedImages)
 },
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 ) {
 Text("Simpan Produk")
 }
 }
 }

 // Variant Dialog
 if (showVariantDialog) {
 var variantName by remember { mutableStateOf("") }
 var variantPrice by remember { mutableStateOf("") }
 var variantStock by remember { mutableStateOf("") }

 AlertDialog(
 onDismissRequest = { showVariantDialog = false },
 title = { Text("Tambah Varian") },
 text = {
 Column {
 OutlinedTextField(
 value = variantName,
 onValueChange = { variantName = it },
 label = { Text("Nama Varian") }
 )
 OutlinedTextField(
 value = variantPrice,
 onValueChange = { variantPrice = it },
 label = { Text("Harga") },
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number)
 )
 OutlinedTextField(
 value = variantStock,
 onValueChange = { variantStock = it },
 label = { Text("Stok") },
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number)
 )
 }
 },
 confirmButton = {
 Button(onClick = {
 val newVariant = ProductVariant(
 name = variantName,
 price = variantPrice.toDoubleOrNull() ?: 0.0,
 stock = variantStock.toIntOrNull() ?: 0
 )
 variants = variants + newVariant
 showVariantDialog = false
 }) {
 Text("Tambah")
 }
 },
 dismissButton = {
 TextButton(onClick = { showVariantDialog = false }) {
 Text("Batal")
 }
 }
 )
 }
}
```

Beberapa poin penting dari implementasi di atas:

1. Screen ini mencakup semua field yang ada di `Product` data class
2. Terdapat fitur untuk menambah varian produk melalui dialog
3. Implementasi pemilihan kategori dan subkategori
4. Input lokasi produk
5. Upload multiple images
6. Validasi input (harga dan stok menggunakan keyboard numerik)

Untuk menggunakan screen ini, Anda perlu:

1. Pastikan semua dependencies terinstal (Material3, Coil, dll)
2. Tambahkan permission untuk akses storage di AndroidManifest.xml
3. Pastikan ProductViewModel sudah diinisialisasi dengan benar
4. Implementasikan fungsi getCurrentUserId() di ProductViewModel

Apakah ada bagian yang ingin Anda tanyakan lebih lanjut?"}]}
